from turtle import Screenfrom snake import Snakefrom food import Foodfrom scoreboard import Scoreboardimport time# Set up the screenscreen = Screen()screen.setup(width=600, height=600)screen.bgcolor("black")screen.title("Snaky the Snake")screen.tracer(0)# Create a snake body, food, and scoreboardsnaky = Snake()food = Food()scoreboard = Scoreboard()screen.listen()screen.onkey(snaky.up, "Up")screen.onkey(snaky.down, "Down")screen.onkey(snaky.left, "Left")screen.onkey(snaky.right, "Right")# Move the snakegame_is_on = Truewhile game_is_on:    screen.update()    time.sleep(0.1)    snaky.move()    # Detect collision with food    if snaky.head.distance(food) < 14:        food.refresh_location()        snaky.extend()        scoreboard.plus_score()    # Detect if snake hit the screen/wall, the game end    if snaky.head.xcor() > 280 or snaky.head.ycor() < -280 or snaky.head.ycor() > 280 or snaky.head.ycor() < -280:        scoreboard.game_over()        game_is_on = False    # Detect collision with tail    for segment in snaky.snakes[1:]:        if snaky.head.distance(segment) < 10:            game_is_on = False            scoreboard.game_over()screen.exitonclick()